function toonArray() {
    let T1 = [4,8,7,9,1,5,4,6];
    let T2 = [7,6];
    let toon = 0;
    for (let i = 0; i <= T2.length; i++) {
        for (let i2 = 0; i2 <= T1.length; i2++) {
            toon += (T2[i2] * T1[i]);
        }
    }
}

