let N = [];
N[0] = 0;
let iteration = 1;
let Npos = 0;
let Nneg = 0;
function arrayPosNeg(arg) {
    if (N[0] < 1) {
        N[0] = parseInt(arg[0].value);
        $('#consigne').html('Entrez la valeur '+iteration);
        $('input[name=num]').val("");
    }else{
        if (iteration >= N[0]){
            //on a terminer, on affiche le résultat
            $('#consigne').html('Il y a '+Npos+' valeur positive et '+Nneg+' valeur négative.');
            $('input[name=num]').remove();
        }else{
            N[iteration] = parseInt(arg[0].value);
            if (N[iteration] > 0){
                Npos++;
            }else {
                Nneg++;
            }
            iteration++;
            $('#consigne').html('Entrez la valeur '+iteration);
            $('input[name=num]').val("");
        }
    }
}

js:
let N = [];
N[0] = 0;
let iteration = 1;
let Npos = 0;
let Nneg = 0;
function arrayPosNeg(arg) {
    if (N[0] < 1) {
        N[0] = parseInt(arg[0].value);
        document.getElementById('consigne').innerHTML = 'Entrez la valeur '+iteration;
    }else{
        if (iteration >= N[0]){
            //on a terminer, on affiche le résultat
            document.getElementById('consigne').innerHTML = 'Il y a '+Npos+' valeur positive et '+Nneg+' valeur négative.'
        }else{
            N[iteration] = parseInt(arg[0].value);
            if (N[iteration] > 0){
                Npos++;
            }else {
                Nneg++;
            }
            iteration++;
            document.getElementById('consigne').innerHTML = 'Entrez la valeur '+iteration;
        }
    }
}
