js:
function suiteBulle(arg) {
    tab[iterationZero] = parseInt(arg[0].value);
    let changer = true;

    while (changer) {
        changer = false;

        for (let i = 0; i < tab.length; i++)
        {
            if (tab[i] < tab[i + 1])
            {
                console.log(tab[i]+" : "+tab[i + 1])
                let temp = tab[i];
                tab[i] = tab[i + 1];
                tab[i + 1] = temp;
                changer = true;

            }
        }
    }
    iterationZero++;
    $('#resolve').html("Le tableau en trie par bulle est : "+tab);
}

