function arrayReverse() {
    let tab2 = [2,43,25,23,52,76];
    let maxTab = tab2.length - 1;
    for (let j = 0; maxTab >= tab2.length/2; maxTab--) {
        let Temp = tab2[j]
        tab2[j] = tab2[maxTab]
        tab2[maxTab] = Temp
        j++;
        console.log(tab2)
    }
    document.getElementById('resolve').innerText = "Le tableau inverse est : " + tab2;
}
