function arraySupIndex(arg) {
    tab = [2,43,25,23,52,76]
    let indexSup = parseInt(arg[0].value);
    for (i = indexSup;i <= tab.length - 2;i++) {
        tab[i] = tab[i + 1];
    }
    delete tab[i];
    $('#resolve').html("Le nouveau tableau est : "+tab);
}

