function arrayTab() {
    let tab = [];
    for (let j = 0; j < 5; j++) {
        tab[j] = [];
        for (let k = 0; k < 12; k++) {
            tab[j][k] = 0;
        }
    }
    $('#resolve').html("Le tableau est : <br/>");
    tab.forEach((tab)=>{
        $('#resolve').append(tab+"<br/>");
    })
}

